mapper.update:
UPDATE treturnmeter
FReturnBatch=#{freturnbatch}, FMeterCode=#{fmetercode}, FCustomer=#{fcustomer}, FMeterName=#{fmetername}, FMeterDirection=#{fmeterdirection}, FDateTime=#{fdatetime}, FOperator=#{foperator}, FReportMisFune=#{freportmisfune}, FMeterReading=#{fmeterreading}, FProduceTime=#{fproducetime}, FSaleTime=#{fsaletime}, FPreMaintain=#{fpremaintain}
WHERE ID=#{id};

mapper.delete:
UPDATE treturnmeter
SET IsDelete=1
WHERE ID=#{id};

mapper.select:
SELECT 
FROM treturnmeter
WHERE IsDelete=0 ORDER BY ID DESC;

SELECT 
FROM treturnmeter
WHERE IsDelete=0 AND FOperator=#{fanalysor};

SELECT DISTINCT freturnbatch
FROM treturnmeter
WHERE IsDelete=0 AND FDateTime=CURRENT_DATE;

SELECT DISTINCT freturnbatch
FROM treturnmeter
WHERE IsDelete=0;

SELECT 
FROM treturnmeter
WHERE IsDelete=0  ORDER BY id DESC;

SELECT 
FROM treturnmeter

    IsDelete=0
    AND FReturnBatch=#{freturnbatch}
    AND FMeterCode=#{fmetercode}
    AND FOperator=#{foperator}
    AND FCustomer=#{fcustomer}
    AND FMeterName=#{fmetername}
    AND FMeterDirection=#{fmeterdirection}
    AND FReportMisFune=#{freportmisfune}
     AND FDateTime>=#{startTime}
     AND FDateTime<=#{endTime}

SELECT 
FROM treturnmeter

    IsDelete=0
    AND FReturnBatch=#{freturnbatch}
    AND FMeterCode=#{fmetercode}
     AND FDateTime>=#{startTime}
     AND FDateTime<=#{endTime}  
    AND FOperator=#{fanalysor}

ORDER BY ID DESC;

SELECT 
FROM treturnmeter

    IsDelete=0
    AND FOperator=#{foperator}
    AND FCustomer=#{fcustomer}
    AND FMeterName=#{fmetername}
     AND FDateTime>=#{startTime}
     AND FDateTime<=#{endTime}

SELECT fmetercode,fmeterreading,freportmisfune,fpremaintain
FROM Dispatch
WHERE fmetercode=#{fmetercode} ORDER BY ID DESC limit 1;

SELECT COUNT(DISTINCT FReturnBatch) AS returnbatchnum,COUNT(DISTINCT fmetercode) AS returnmeternum
FROM treturnmeter

    IsDelete=0
    AND FCustomer=#{fcustomer}
    AND FMeterName=#{fmetername}
     AND FDateTime>=#{startTime}
     AND FDateTime<=#{endTime}

SELECT COUNT(DISTINCT treturnmeter.fmetercode) AS analysisnum
FROM treturnmeter
JOIN tmeteranalysisinfo ON treturnmeter.fmetercode=tmeteranalysisinfo.fmetercode

    treturnmeter.IsDelete=0 AND tmeteranalysisinfo.IsDelete=0
    AND treturnmeter.FCustomer=#{fcustomer}
    AND treturnmeter.FMeterName=#{fmetername}
     AND treturnmeter.FDateTime>=#{startTime}
     AND treturnmeter.FDateTime<=#{endTime}
